import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const [showOtpInput, setShowOtpInput] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Configure API base URL
  const API_URL = 'http://localhost:5000/api/auth';

  // Handle login - send OTP to email
  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    try {
      // First, try to login with email/password
      const loginResponse = await axios.post(`${API_URL}/login`, {
        email,
        password
      });
      
      // If login successful, show OTP input
      if (loginResponse.data) {
        setShowOtpInput(true);
        setMessage('Login successful! Please enter the OTP sent to your email.');
      }
    } catch (err) {
      // If login fails, check if user wants to register or try OTP
      if (err.response?.status === 401) {
        setShowOtpInput(true);
        setMessage('Please enter the OTP sent to your email for verification.');
      } else {
        setError(err.response?.data?.message || 'Login failed');
      }
    }
  };

  // Handle OTP verification
  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');

    try {
      const response = await axios.post(`${API_URL}/verify-otp`, {
        email,
        otp
      });

      if (response.data.token) {
        setIsLoggedIn(true);
        setMessage('OTP verified successfully! You are now logged in.');
        // Store token in localStorage
        localStorage.setItem('token', response.data.token);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'OTP verification failed');
    }
  };

  // Handle send OTP (for resending)
  const handleSendOtp = async () => {
    setError('');
    setMessage('');

    try {
      const response = await axios.post(`${API_URL}/send-otp`, {
        email
      });
      setMessage(response.data.message || 'OTP sent successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to send OTP');
    }
  };

  // Handle logout
  const handleLogout = () => {
    setIsLoggedIn(false);
    setEmail('');
    setPassword('');
    setOtp('');
    setShowOtpInput(false);
    setMessage('');
    localStorage.removeItem('token');
  };

  // Render login form
  const renderLoginForm = () => (
    <div className="login-container">
      <div className="login-box">
        <h1>CreatorConnect</h1>
        <h2>Login</h2>
        
        {!showOtpInput ? (
          <form onSubmit={handleLogin}>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
            <button type="submit" className="btn-primary">
              Login
            </button>
          </form>
        ) : (
          <form onSubmit={handleVerifyOtp}>
            <div className="form-group">
              <label>Enter OTP</label>
              <input
                type="text"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                placeholder="Enter 6-digit OTP"
                maxLength="6"
                required
              />
            </div>
            <button type="submit" className="btn-primary">
              Verify OTP
            </button>
            <button 
              type="button" 
              className="btn-secondary"
              onClick={handleSendOtp}
            >
              Resend OTP
            </button>
          </form>
        )}

        {message && <div className="message success">{message}</div>}
        {error && <div className="message error">{error}</div>}
      </div>
    </div>
  );

  // Render logged in view
  const renderLoggedIn = () => (
    <div className="login-container">
      <div className="login-box">
        <h1>Welcome to CreatorConnect!</h1>
        <p>You have successfully logged in.</p>
        <button onClick={handleLogout} className="btn-primary">
          Logout
        </button>
      </div>
    </div>
  );

  return (
    <div className="App">
      {isLoggedIn ? renderLoggedIn() : renderLoginForm()}
    </div>
  );
}

export default App;
