import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import App from './App';
import './index.css';

// Some browser extensions inject floating "Reply" widgets into pages.
// Keep app UI clean by hiding only suspicious non-app overlays.
const hideInjectedReplyOverlays = () => {
  const rootEl = document.getElementById('root');
  if (!rootEl) return;

  const isSuspiciousOverlay = (node) => {
    if (!(node instanceof HTMLElement)) return false;
    if (node === rootEl || rootEl.contains(node)) return false;

    const text = (node.innerText || '').toLowerCase().trim();
    if (!text || !text.includes('reply')) return false;

    const style = window.getComputedStyle(node);
    const isFloating = style.position === 'fixed' || style.position === 'absolute' || style.position === 'sticky';
    const isVisible = style.display !== 'none' && style.visibility !== 'hidden' && style.opacity !== '0';

    return isFloating && isVisible;
  };

  const hideNode = (node) => {
    if (!(node instanceof HTMLElement)) return;
    node.style.setProperty('display', 'none', 'important');
  };

  const scanAndHide = (container) => {
    if (!(container instanceof Element)) return;
    if (isSuspiciousOverlay(container)) {
      hideNode(container);
    }
    container.querySelectorAll('*').forEach((el) => {
      if (isSuspiciousOverlay(el)) hideNode(el);
    });
  };

  scanAndHide(document.body);

  const observer = new MutationObserver((mutations) => {
    mutations.forEach((mutation) => {
      mutation.addedNodes.forEach((addedNode) => {
        scanAndHide(addedNode);
      });
    });
  });

  observer.observe(document.body, { childList: true, subtree: true });
};

hideInjectedReplyOverlays();

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
      <Toaster
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            duration: 3000,
            iconTheme: {
              primary: '#10b981',
              secondary: '#fff',
            },
          },
          error: {
            duration: 4000,
            iconTheme: {
              primary: '#ef4444',
              secondary: '#fff',
            },
          },
        }}
      />
    </BrowserRouter>
  </React.StrictMode>
);
