import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import App from './App';
import './index.css';

// Some browser extensions inject floating "Reply" widgets into pages.
// Keep app UI clean by hiding suspicious floating overlays (including shadow DOM).
const hideInjectedReplyOverlays = () => {
  const rootEl = document.getElementById('root');
  if (!rootEl) return;

  const shouldHideElement = (node) => {
    if (!(node instanceof HTMLElement)) return false;

    const text = (node.innerText || '').toLowerCase().trim();
    if (!text.includes('reply')) return false;

    const style = window.getComputedStyle(node);
    const isFloating = style.position === 'fixed' || style.position === 'absolute' || style.position === 'sticky';
    const isVisible = style.display !== 'none' && style.visibility !== 'hidden' && style.opacity !== '0';
    if (!isFloating || !isVisible) return false;

    const rect = node.getBoundingClientRect();
    const nearTopRight =
      rect.top < 260 &&
      window.innerWidth - rect.right < 320;

    return nearTopRight;
  };

  const hideNode = (node) => {
    if (!(node instanceof HTMLElement)) return;
    node.style.setProperty('display', 'none', 'important');
  };

  const scanRoot = (container) => {
    if (!(container instanceof Element) && !(container instanceof Document) && !(container instanceof ShadowRoot)) {
      return;
    }

    const elements = container.querySelectorAll ? container.querySelectorAll('*') : [];
    elements.forEach((el) => {
      if (!(el instanceof HTMLElement)) return;

      if (shouldHideElement(el)) {
        hideNode(el);
        // Also hide floating parent wrapper if present.
        const floatingParent = el.closest('*');
        if (floatingParent && floatingParent instanceof HTMLElement) {
          const parentStyle = window.getComputedStyle(floatingParent);
          const isParentFloating =
            parentStyle.position === 'fixed' ||
            parentStyle.position === 'absolute' ||
            parentStyle.position === 'sticky';
          if (isParentFloating) hideNode(floatingParent);
        }
      }

      if (el.shadowRoot) {
        scanRoot(el.shadowRoot);
      }
    });
  };

  const runScan = () => scanRoot(document);
  runScan();

  const observer = new MutationObserver((mutations) => {
    mutations.forEach((mutation) => {
      mutation.addedNodes.forEach((addedNode) => {
        if (addedNode instanceof HTMLElement && addedNode.shadowRoot) {
          scanRoot(addedNode.shadowRoot);
        }
        if (addedNode instanceof Element || addedNode instanceof DocumentFragment) {
          scanRoot(addedNode);
        }
      });
    });
  });

  observer.observe(document.body, { childList: true, subtree: true });

  // Extensions may re-inject repeatedly.
  setInterval(runScan, 1200);
};

hideInjectedReplyOverlays();

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <BrowserRouter>
      <App />
      <Toaster
        position="top-right"
        toastOptions={{
          duration: 4000,
          style: {
            background: '#363636',
            color: '#fff',
          },
          success: {
            duration: 3000,
            iconTheme: {
              primary: '#10b981',
              secondary: '#fff',
            },
          },
          error: {
            duration: 4000,
            iconTheme: {
              primary: '#ef4444',
              secondary: '#fff',
            },
          },
        }}
      />
    </BrowserRouter>
  </React.StrictMode>
);
